 .print-format table {
            width: 98%!important;
            border-collapse: collapse!important;
        }
.print-format td {
            border: 1px solid #dddddd!important;
            text-align: left!important;
            padding: 8px!important;
            }

        /* Style for even rows (starting from 0) */
        .print-format tr:nth-child(even) {
            background-color: #f2f2f2!important;
        }

        /* Style for odd rows */
        .print-format tr:nth-child(odd) {
            background-color: #ffffff!important;
        }
        .print-format {
    font-size: 14px!important;
    background: none!important;
}

   .print-format .project-data td:nth-child(odd) {
            font-weight: bolder!important;
        }

        /* Style for odd rows */
        .print-format .project-data td:nth-child(even) {
\        }
        .print-format .point-table tr:first-child {
            background-color: #4c86d2!important;
        }
        .print-format .point-table td:first-child {
            font-weight: bolder!important;

        }
.print-format th {
            background-color: #28639d;
            color: #ffffff;
            font-weight: bold;
        }

